headerButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
//let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openAddDeviceDialog();

function openAddDeviceDialog() {
    customDialog.customDialog(htmlTemplate, AddDeviceDialogController).subscribe();
}

function AddDeviceDialogController(instance) {
    let vm = instance;

    vm.addDeviceFormGroup = vm.fb.group({
      serialNumber: ['', [vm.validators.required]],
      deviceName: ['', [vm.validators.required]],
      deviceType: ['', [vm.validators.required]]
      /*
      deviceLabel: [''],
      attributes: vm.fb.group({
          latitude: [null],
          longitude: [null]
      })
      */
    });

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        vm.addDeviceFormGroup.markAsPristine();
        let device = {
            name: vm.addDeviceFormGroup.get('deviceName').value,
            type: vm.addDeviceFormGroup.get('deviceType').value,
            label: vm.addDeviceFormGroup.get('deviceName').value
        };
        deviceService.saveDevice(device).subscribe(
            function (device) {

                deviceService.getDeviceCredentials(device.id.id, true).subscribe(function (deviceCredentials) {

                    deviceCredentials.credentialsId = vm.addDeviceFormGroup.get('serialNumber').value;

                    deviceService.saveDeviceCredentials(deviceCredentials).subscribe(function () {
                        vm.dialogRef.close(null) 
                    });
                });


                /*saveAttributes(device.id).subscribe(
                    function () {
                        widgetContext.updateAliases();
                        vm.dialogRef.close(null);
                    }
                );*/
            }
        );
    };

    /*function saveAttributes(entityId) {
