var SensorStatus   = msg.temperatureWater_status;
var temperature    = msg.temperatureWater;
var alarmType      = "Temperatura da agua";
var message        = "Temperatura da agua em " + temperature + "°C";

// Função para calcular a diferença de tempo em minutos
function calcularDiferencaTempoEmMinutos(data) {
    var primeiraData = data[0].ts; // Primeiro timestamp
    var ultimaData = data[data.length - 1].ts; // Último timestamp
    var diferencaEmMinutos = (ultimaData - primeiraData) / (1000 * 60); // Calcula a diferença em minutos
    return diferencaEmMinutos;
}

// Função para verificar se todos os valores são "OPEN"
function todosSao(data, diferente) {
    for (var i = 0; i < data.length; i++) {
        if (data[i].value == diferente) {
            return false; // Retorna falso se encontrar qualquer valor diferente de "x"
        }
    }
    return true; // Retorna verdadeiro se todos os valores forem "OPEN"
}

// Função para verificar se todos os valores são "OPEN"
function todosSaoMaior(data, maior) {
    for (var i = 0; i < data.length; i++) {
        if (data[i].value < maior) {
            return false; // Retorna falso se encontrar qualquer valor maior de "x"
        }
    }
    return true; // Retorna verdadeiro se todos os valores forem "OPEN"
}

// Função para verificar se todos os valores são "OPEN"
function todosSaoMenor(data, menor) {
    for (var i = 0; i < data.length; i++) {
        if (data[i].value > menor) {
            return false; // Retorna falso se encontrar qualquer valor maior de "x"
        }
    }
    return true; // Retorna verdadeiro se todos os valores forem "OPEN"
}

var temperaturedelta   = JSON.parse(metadata.temperatureWater);
var diferencaEmMinutos = calcularDiferencaTempoEmMinutos(temperaturedelta);
var todosSaoMaior      = todosSaoMaior(temperaturedelta, 50);
var todosSaoMenor      = todosSaoMenor(temperaturedelta, 20);

