if (temperature > 50 && todosSaoMaior){
    var alertMessage  = "A temperatura da água está ALTA há "+ diferencaEmMinutos + " min." + temperature + "°C ";
    var isolatedStatus = "ALERTA";

    msg.status = {
        errorStatus: isolatedStatus,
        message: alertMessage,
        alarmType: alarmType,
    };        
}

if (temperature < 20 && todosSaoMenor){
    var alertMessage  = "A temperatura da água está BAIXA há "+ diferencaEmMinutos + " min." + temperature + "°C ";
    var isolatedStatus = "ALERTA";

    msg.status = {
        errorStatus: isolatedStatus,
        message: alertMessage,
        alarmType: alarmType,
    };        
