var isolatedStatus = msg.doorSensor_status;
var stateDoor      = msg.doorSensor;
var alarmType;
var status         = "ALERTA";

// Função para calcular a diferença de tempo em minutos
function calcularDiferencaTempoEmMinutos(data) {
    var primeiraData = data[0].ts; // Primeiro timestamp
    var ultimaData = data[data.length - 1].ts; // Último timestamp
    var diferencaEmMinutos = (ultimaData - primeiraData) / (1000 * 60); // Calcula a diferença em minutos
    return diferencaEmMinutos;
}

// Função para verificar se todos os valores são "OPEN"
function todosSaoOPEN(data) {
    for (var i = 0; i < data.length; i++) {
        if (data[i].value !== "OPEN") {
            return false; // Retorna falso se encontrar qualquer valor diferente de "OPEN"
        }
    }
    return true; // Retorna verdadeiro se todos os valores forem "OPEN"
}

var deltadoor          = JSON.parse(metadata.doorSensor);
var diferencaEmMinutos = calcularDiferencaTempoEmMinutos(deltadoor);
var todosOPEN          = todosSaoOPEN(deltadoor);

msg.deltadoor = diferencaEmMinutos;
msg.disparar  = todosOPEN;

if (isolatedStatus.trim() != "OK" ) {
    var alertMessage  = "Sensor de porta em alerta";
    alarmType = "SENSOR REPORTOU PROBLEMA";
    msg.status = {
        errorStatus: status,
        message: alertMessage,
        alarmType: alarmType
    };
    msg.sensorproblem= true;
}


if (stateDoor.trim() != "CLOSED" && todosOPEN){
    var alertMessage  = "A Porta do equipamento em ALERTA: " + stateDoor + " há " + diferencaEmMinutos+ " Minutos";
    alarmType = "PORTA ABERTA";
    msg.status = {
        errorStatus: status,
        message: alertMessage,
        alarmType: alarmType
    };        

    msg.sensorproblem= false;
}

return {msg: msg, metadata: metadata, msgType: msgType};

