    var primeiraData = data[0].ts; // Primeiro timestamp
    var ultimaData = data[data.length - 1].ts; // Último timestamp
    var diferencaEmMinutos = (ultimaData - primeiraData) / (1000 * 60); // Calcula a diferença em minutos
    return diferencaEmMinutos;
}

